package parimala;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=5;
		long result=1;
		for(int i=1;i<=num;i++) {
			result=result*i;
		}
		System.out.println("1*2*3*4*5");
		System.out.println("factorial is"+result);
		}

	}


