public class Positivenegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=556;
if(i>0) {
	System.out.println(i+"is positive");
	
}
else if(i<0) {
	System.out.println(i+"is negative");
	
}
else {
	System.out.println(i+"is zero");
}
	}

}
